    /* bootstrap, will call initialize() */
    function WMESP_bootstrap() {
        log('Init');
        /* begin running the code! */
        setTimeout(initialize, 1000);
    }

    // ==========  Helper ==============================//
    function getElementsByClassName(classname, node) {
        if (!node) node = document.getElementsByTagName('body')[0];
        const a = [];
        const re = new RegExp(`\\b${classname}\\b`);
        const els = node.getElementsByTagName('*');
        for (let i = 0, j = els.length; i < j; i++) {
            if (re.test(els[i].className)) a.push(els[i]);
        }
        return a;
    }

    function getId(node) {
        return document.getElementById(node);
    }

    function log(msg, obj) {
        if (obj == null) {
            console.log(`WME Split POI v${SCRIPT_VERSION} - ${msg}`);
        } else if (DEBUG) {
            console.debug(`WME Split POI v${SCRIPT_VERSION} - ${msg} `, obj);
        }
    }

